mapper.select:
select * from todo where status='0' and (starttime is null or starttime < CURRENT_TIMESTAMP(0))

		and type=#{type}

order by priority,inserttime

select * from todo where status='0'

	and type=#{type}

order by priority,inserttime

select * from todo where status='1'

	and finishtime > #{start}


	and finishtime < #{end}


	and content like concat('%',#{keyword},'%')


	and type=#{type}

order by finishtime desc
